mapper.select:

	SELECT COUNT(MID) AS RESULT
	FROM MSM_MEMBER
	WHERE MID = #{mId}


	SELECT COUNT(*)
	FROM MSM_MEMBER
	WHERE MID = #{mId}


	SELECT M.MID, A.AUTHORITY
	FROM MSM_MEMBER M, AUTHORITIES A 
	WHERE M.MID = #{mId} AND A.AUTHORITYNAME = #{authority}

mapper.update:

	UPDATE MSM_MEMBER
	SET AUTHORITYNAME = #{authority}
	WHERE MID = #{mId}
