mapper.select:

	SELECT NVL(SUM(MGPRICE * AMOUNT), 0) MONEY
	FROM EX_CART C, EX_GOODS P
	WHERE C.MGNUM = P.MGNUM AND C.USER_ID = #{userId}

mapper.update:

	UPDATE EX_CART
	SET AMOUNT = #{amount}
	WHERE USER_ID = #{userId}
	AND MGNUM = #{mgNum}

mapper.select:
SELECT COUNT(*)
FROM EX_CART
WHERE USER_ID = #{userId}
AND MGNUM = #{mgNum}

mapper.update:
UPDATE EX_CART
SET AMOUNT = AMOUNT + #{amount}
WHERE USER_ID = #{userId}
AND MGNUM = #{mgNum}

mapper.select:
select m.mzip_code as mZip_Code, m.m_first_addr as mFirst_Addr, m.m_second_addr as mSecond_Addr, m.mId, m.mPhone
from msm_member m,ex_cart c
where m.mid = c.user_id and c.user_id=#{userId}
