mapper.select:

	SELECT NVL(SUM(MGPRICE * AMOUNT), 0) MONEY
	FROM EX_CART C, EX_GOODS P
	WHERE C.MGNUM = P.MGNUM AND C.USER_ID = #{userId}

mapper.update:

	UPDATE EX_CART
	SET AMOUNT = #{amount}
	WHERE USER_ID = #{userId}
	AND MGNUM = #{mgNum}

mapper.select:
SELECT COUNT(*)
FROM EX_CART
WHERE USER_ID = #{userId}
AND MGNUM = #{mgNum}

mapper.update:
UPDATE EX_CART
SET AMOUNT = AMOUNT + #{amount}
WHERE USER_ID = #{userId}
AND MGNUM = #{mgNum}
